import React, { useState, useEffect } from 'react';

import Card from './Card';
import Slide from './Slide';

import '../styles.css';

const Content = ({ heightOnPage }) => {
  const divDefault = { display: 'contents' }

  const [lastYPod, setLastYPos] = useState(0);
  const [imgCurrent, setImgCurrent] = useState(0);
  const [isAnimated, setIsAnimated] = useState(false);
  // const [el, setEl] = useState('');
  // const [clicked, setClicked] = useState(0);

  const imgSrc = [
    ['https://image.flaticon.com/icons/svg/789/789395.svg'],
    ['https://img.ibxk.com.br/2017/07/13/13160112901226.jpg'],
    ['https://upload.wikimedia.org/wikipedia/commons/thumb/e/ec/Mona_Lisa%2C_by_Leonardo_da_Vinci%2C_from_C2RMF_retouched.jpg/180px-Mona_Lisa%2C_by_Leonardo_da_Vinci%2C_from_C2RMF_retouched.jpg'],
    ['https://i.uai.com.br/2N3uPUi_VZQFKLcJSlGCY_cxE2Y=/750x0/imgsapp2.uai.com.br/app/noticia_133890394703/2018/03/19/224026/20180319132735227007e.jpg'],
    ['data:image/jpeg;base64,'],
    ['data:image/jpeg;base64,'],
    ['http://bsnscb.com/data/out/82/40159224-full-hd-wallpapers.jpeg'],
    ['https://i.pinimg.com/236x/d8/a0/72/d8a0727ecf87ab57f431000587eee2a0--golden-box-the-golden.jpg'],
    ['https://cmd.mg.gov.br/wp-content/uploads/2018/08/cartaz-final-1-1.jpg'],
    ['https://www.cartacapital.com.br/wp-content/uploads/2020/01/planeta-terra-1200x800.jpg'],
    ['https://cdnstatic8.com/apureguria.com/wp-content/uploads/2016/06/castelo-marienburg-alemanha.jpg'],
    ['https://abrilveja.files.wordpress.com/2020/01/filme-1917-1.jpg?quality=70&strip=info&resize=680,453'],

  ];

  // Hidden Effect
  useEffect(() => {
    function Hand() {
      const a = window.scrollY
      setLastYPos(a)
    }
    window.addEventListener("scroll", Hand, false);

    const $part = document.querySelectorAll('.content-container')[0]
    window.addEventListener('scroll', hiddenItem, false)
    function hiddenItem() {
      if (window.scrollY > heightOnPage) {
        $part.classList.remove('notAnim');
        $part.classList.add('Anim');
      }
      else if (window.scrollY <= heightOnPage) {
        $part.classList.remove('Anim');
        $part.classList.add('notAnim');
      }
    }

    return () => {
      window.removeEventListener("scroll", Hand, false);
    };

  }, [lastYPod])

  // Get Image Slide
  useEffect(() => {
    const $div = document.querySelectorAll('#content-divImg');
    $div.forEach(element => {
      element.addEventListener('click', () => {
        setImgCurrent(parseInt(element.getAttribute('data-image')))
        
        return; 
      })
    })
    
  }, [])
  
  const actionSlide = () => setIsAnimated(!isAnimated);

  return (
    <section id='content' style={{ backgroundColor: '#4c4646' }} >
      {isAnimated && <Slide actionSlide={actionSlide} imgSrc={imgSrc} imgCurrent={imgCurrent} /> }
      <h1 style={{ color: '#fff', textAlign: "center", paddingTop: '5rem' }} >Look us services</h1>
      <div className='container content-container notAnim' >
        <div className='col-md-12 row' style={{ justifyContent: 'center' }}>
          {imgSrc.map((content, index) => (
            <div 
              key={index} 
              // onClick={handleSlide} 
              className='col-md-4' 
              style={divDefault}
              id='content-divImg'
              data-image={index}
              onClick={actionSlide}
            >
              <Card img={content} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Content;